import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);
  const [machines, setMachines] = useState([]);
  const [userForm, setUserForm] = useState({
    name: "",
    role: "",
    email: "",
    phone: ""
  });
  const [machineForm, setMachineForm] = useState({
    name: "",
    type: "",
    status: "",
    location: ""
  });

  useEffect(() => {
    fetchUsers();
    fetchMachines();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Kullanıcılar çekilemedi:", error);
    }
  };

  const fetchMachines = async () => {
    try {
      const response = await axios.get("http://localhost:5000/machines");
      setMachines(response.data);
    } catch (error) {
      console.error("Makineler çekilemedi:", error);
    }
  };

  const handleUserInputChange = (e) => {
    setUserForm({ ...userForm, [e.target.name]: e.target.value });
  };

  const handleMachineInputChange = (e) => {
    setMachineForm({ ...machineForm, [e.target.name]: e.target.value });
  };

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", userForm);
      setUserForm({ name: "", role: "", email: "", phone: "" });
      fetchUsers();
    } catch (error) {
      console.error("Kullanıcı eklenemedi:", error);
    }
  };

  const handleMachineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/machines", machineForm);
      setMachineForm({ name: "", type: "", status: "", location: "" });
      fetchMachines();
    } catch (error) {
      console.error("Makine eklenemedi:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Akıllı Talep ve Arıza Yönetimi</h1>

      {/* Kullanıcı Ekleme Formu */}
      <h2>Kullanıcı Ekle</h2>
      <form onSubmit={handleUserSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          name="name"
          placeholder="Ad Soyad"
          value={userForm.name}
          onChange={handleUserInputChange}
          required
        />
        <input
          type="text"
          name="role"
          placeholder="Rol"
          value={userForm.role}
          onChange={handleUserInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={userForm.email}
          onChange={handleUserInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="text"
          name="phone"
          placeholder="Telefon"
          value={userForm.phone}
          onChange={handleUserInputChange}
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Kaydet
        </button>
      </form>

      {/* Kullanıcı Listesi */}
      <h3>Kullanıcı Listesi</h3>
      <ul>
        {users.length > 0 ? (
          users.map((user) => (
            <li key={user.id}>
              {user.name} - {user.role} - {user.email} - {user.phone}
            </li>
          ))
        ) : (
          <p>Henüz kullanıcı bulunamadı.</p>
        )}
      </ul>

      {/* Makine Ekleme Formu */}
      <h2>Makine Ekle</h2>
      <form onSubmit={handleMachineSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          name="name"
          placeholder="Makine Adı"
          value={machineForm.name}
          onChange={handleMachineInputChange}
          required
        />
        <input
          type="text"
          name="type"
          placeholder="Makine Tipi"
          value={machineForm.type}
          onChange={handleMachineInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="text"
          name="status"
          placeholder="Durum (active, maintenance, broken)"
          value={machineForm.status}
          onChange={handleMachineInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="text"
          name="location"
          placeholder="Lokasyon"
          value={machineForm.location}
          onChange={handleMachineInputChange}
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>
          Kaydet
        </button>
      </form>

      {/* Makine Listesi */}
      <h3>Makine Listesi</h3>
      <ul>
        {machines.length > 0 ? (
          machines.map((machine) => (
            <li key={machine.id}>
              {machine.name} - {machine.type} - {machine.status} - {machine.location}
            </li>
          ))
        ) : (
          <p>Henüz makine bulunamadı.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
