import React, { useEffect, useState } from "react";
import axios from "axios";

function DashboardPage() {
  const [userCount, setUserCount] = useState(0);
  const [machineCount, setMachineCount] = useState(0);
  const [faultCount, setFaultCount] = useState(0);

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      const users = await axios.get("http://localhost:5000/users");
      const machines = await axios.get("http://localhost:5000/machines");
      const faults = await axios.get("http://localhost:5000/faults");

      setUserCount(users.data.length);
      setMachineCount(machines.data.length);
      setFaultCount(faults.data.length);
    } catch (error) {
      console.error("Dashboard verileri çekilemedi:", error);
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "space-around", marginTop: "40px" }}>
      <div style={cardStyle}>
        <h3>Toplam Kullanıcı</h3>
        <p style={countStyle}>{userCount}</p>
      </div>
      <div style={cardStyle}>
        <h3>Toplam Makine</h3>
        <p style={countStyle}>{machineCount}</p>
      </div>
      <div style={cardStyle}>
        <h3>Toplam Arıza</h3>
        <p style={countStyle}>{faultCount}</p>
      </div>
    </div>
  );
}

const cardStyle = {
  backgroundColor: "#ffffff",
  padding: "30px",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  textAlign: "center",
  width: "250px"
};

const countStyle = {
  fontSize: "36px",
  color: "#4CAF50",
  marginTop: "10px"
};

export default DashboardPage;
