import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [users, setUsers] = useState([]);
  const [machines, setMachines] = useState([]);
  const [faults, setFaults] = useState([]);
  const [userForm, setUserForm] = useState({ name: "", role: "", email: "", phone: "" });
  const [machineForm, setMachineForm] = useState({ name: "", type: "", status: "", location: "" });
  const [faultForm, setFaultForm] = useState({ machine_id: "", description: "", status: "open" });

  useEffect(() => {
    fetchUsers();
    fetchMachines();
    fetchFaults();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Kullanƒ±cƒ±lar √ßekilemedi:", error);
    }
  };

  const fetchMachines = async () => {
    try {
      const response = await axios.get("http://localhost:5000/machines");
      setMachines(response.data);
    } catch (error) {
      console.error("Makineler √ßekilemedi:", error);
    }
  };

  const fetchFaults = async () => {
    try {
      const response = await axios.get("http://localhost:5000/faults");
      setFaults(response.data);
    } catch (error) {
      console.error("Arƒ±zalar √ßekilemedi:", error);
    }
  };

  const handleUserInputChange = (e) => {
    setUserForm({ ...userForm, [e.target.name]: e.target.value });
  };

  const handleMachineInputChange = (e) => {
    setMachineForm({ ...machineForm, [e.target.name]: e.target.value });
  };

  const handleFaultInputChange = (e) => {
    setFaultForm({ ...faultForm, [e.target.name]: e.target.value });
  };

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", userForm);
      setUserForm({ name: "", role: "", email: "", phone: "" });
      fetchUsers();
      toast.success("Kullanƒ±cƒ± ba≈üarƒ±yla eklendi! üéâ");
    } catch (error) {
      console.error("Kullanƒ±cƒ± eklenemedi:", error);
      toast.error("Kullanƒ±cƒ± eklenemedi ‚ùå");
    }
  };

  const handleMachineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/machines", machineForm);
      setMachineForm({ name: "", type: "", status: "", location: "" });
      fetchMachines();
      toast.success("Makine ba≈üarƒ±yla eklendi! üéâ");
    } catch (error) {
      console.error("Makine eklenemedi:", error);
      toast.error("Makine eklenemedi ‚ùå");
    }
  };

  const handleFaultSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/faults", faultForm);
      setFaultForm({ machine_id: "", description: "", status: "open" });
      fetchFaults();
      toast.success("Arƒ±za ba≈üarƒ±yla kaydedildi! üéâ");
    } catch (error) {
      console.error("Arƒ±za eklenemedi:", error);
      toast.error("Arƒ±za kaydƒ± yapƒ±lamadƒ± ‚ùå");
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
      toast.error("Kullanƒ±cƒ± silindi! üóëÔ∏è");
    } catch (error) {
      console.error("Kullanƒ±cƒ± silinemedi:", error);
      toast.error("Kullanƒ±cƒ± silinemedi ‚ùå");
    }
  };

  const deleteMachine = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/machines/${id}`);
      fetchMachines();
      toast.error("Makine silindi! üóëÔ∏è");
    } catch (error) {
      console.error("Makine silinemedi:", error);
      toast.error("Makine silinemedi ‚ùå");
    }
  };

  const deleteFault = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/faults/${id}`);
      fetchFaults();
      toast.error("Arƒ±za kaydƒ± silindi! üóëÔ∏è");
    } catch (error) {
      console.error("Arƒ±za kaydƒ± silinemedi:", error);
      toast.error("Arƒ±za kaydƒ± silinemedi ‚ùå");
    }
  };

  const resolveFault = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/faults/${id}`, {
        status: "resolved",
        resolved_by: 1
      });
      fetchFaults();
      toast.success("Arƒ±za ba≈üarƒ±yla √ß√∂z√ºld√º! üéØ");
    } catch (error) {
      console.error("Arƒ±za √ß√∂z√ºlemedi:", error);
      toast.error("Arƒ±za √ß√∂z√ºm√ºnde hata olu≈ütu ‚ùå");
    }
  };

  return (
    <div style={{
      padding: "40px",
      backgroundColor: "#f5f7fa",
      minHeight: "100vh",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: "#333333"
    }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>Akƒ±llƒ± Talep ve Arƒ±za Y√∂netimi</h1>

      {/* Kullanƒ±cƒ± Ekleme */}
      <div style={{
        backgroundColor: "#ffffff",
        padding: "20px",
        marginBottom: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h2>Kullanƒ±cƒ± Ekle</h2>
        <form onSubmit={handleUserSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
          <input type="text" name="name" placeholder="Ad Soyad" value={userForm.name} onChange={handleUserInputChange} required />
          <input type="text" name="role" placeholder="Rol" value={userForm.role} onChange={handleUserInputChange} required />
          <input type="email" name="email" placeholder="Email" value={userForm.email} onChange={handleUserInputChange} required />
          <input type="text" name="phone" placeholder="Telefon" value={userForm.phone} onChange={handleUserInputChange} />
          <button type="submit" style={{ backgroundColor: "#4CAF50", color: "#ffffff", border: "none", padding: "10px 20px", borderRadius: "5px", cursor: "pointer" }}>Kaydet</button>
        </form>
      </div>

      {/* Kullanƒ±cƒ± Listesi */}
      <h3>Kullanƒ±cƒ±lar</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {users.map((user) => (
          <li key={user.id} style={{
            backgroundColor: "#ffffff",
            marginBottom: "10px",
            padding: "15px",
            borderRadius: "8px",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center"
          }}>
            <span>{user.name} - {user.role} - {user.email} - {user.phone}</span>
            <button onClick={() => deleteUser(user.id)} style={{
              backgroundColor: "#f44336",
              color: "#ffffff",
              border: "none",
              padding: "8px 12px",
              borderRadius: "5px",
              cursor: "pointer"
            }}>Sil</button>
          </li>
        ))}
      </ul>

      {/* Makine Ekleme */}
<div style={{
  backgroundColor: "#ffffff",
  padding: "20px",
  marginBottom: "30px",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
}}>
  <h2>Makine Ekle</h2>
  <form onSubmit={handleMachineSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
    <input type="text" name="name" placeholder="Makine Adƒ±" value={machineForm.name} onChange={handleMachineInputChange} required />
    <input type="text" name="type" placeholder="T√ºr" value={machineForm.type} onChange={handleMachineInputChange} required />
    <input type="text" name="status" placeholder="Durum" value={machineForm.status} onChange={handleMachineInputChange} />
    <input type="text" name="location" placeholder="Lokasyon" value={machineForm.location} onChange={handleMachineInputChange} />
    <button type="submit" style={{ backgroundColor: "#4CAF50", color: "#ffffff", border: "none", padding: "10px 20px", borderRadius: "5px", cursor: "pointer" }}>Kaydet</button>
  </form>
</div>


      <ToastContainer />
    </div>
  );
}

export default App;
