import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

function UsersPage() {
  const [users, setUsers] = useState([]);
  const [userForm, setUserForm] = useState({ name: "", role: "", email: "", phone: "" });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Kullanƒ±cƒ±lar √ßekilemedi:", error);
    }
  };

  const handleUserInputChange = (e) => {
    setUserForm({ ...userForm, [e.target.name]: e.target.value });
  };

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", userForm);
      setUserForm({ name: "", role: "", email: "", phone: "" });
      fetchUsers();
      toast.success("Kullanƒ±cƒ± ba≈üarƒ±yla eklendi! üéâ");
    } catch (error) {
      console.error("Kullanƒ±cƒ± eklenemedi:", error);
      toast.error("Kullanƒ±cƒ± eklenemedi ‚ùå");
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
      toast.error("Kullanƒ±cƒ± silindi! üóëÔ∏è");
    } catch (error) {
      console.error("Kullanƒ±cƒ± silinemedi:", error);
      toast.error("Kullanƒ±cƒ± silinemedi ‚ùå");
    }
  };

  return (
    <div>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Kullanƒ±cƒ±lar</h2>

      <form onSubmit={handleUserSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px", justifyContent: "center", marginBottom: "30px" }}>
        <input type="text" name="name" placeholder="Ad Soyad" value={userForm.name} onChange={handleUserInputChange} required />
        <input type="text" name="role" placeholder="Rol" value={userForm.role} onChange={handleUserInputChange} required />
        <input type="email" name="email" placeholder="Email" value={userForm.email} onChange={handleUserInputChange} required />
        <input type="text" name="phone" placeholder="Telefon" value={userForm.phone} onChange={handleUserInputChange} />
        <button type="submit" style={{
          backgroundColor: "#4CAF50",
          color: "#ffffff",
          border: "none",
          padding: "10px 20px",
          borderRadius: "5px",
          cursor: "pointer"
        }}>Kaydet</button>
      </form>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", justifyContent: "center" }}>
        {users.map((user) => (
          <div key={user.id} style={{
            backgroundColor: "#ffffff",
            borderRadius: "10px",
            padding: "20px",
            width: "250px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            transition: "transform 0.3s",
            cursor: "pointer",
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = "scale(1.05)";
            e.currentTarget.style.boxShadow = "0 6px 12px rgba(0,0,0,0.15)";
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = "scale(1)";
            e.currentTarget.style.boxShadow = "0 4px 8px rgba(0,0,0,0.1)";
          }}
          
          >
            <h3 style={{ margin: "10px 0" }}>{user.name}</h3>
            <p style={{ margin: "5px 0" }}>{user.role}</p>
            <p style={{ margin: "5px 0" }}>{user.email}</p>
            <p style={{ margin: "5px 0" }}>{user.phone}</p>
            <button onClick={() => deleteUser(user.id)} style={{
              marginTop: "10px",
              backgroundColor: "#f44336",
              color: "#ffffff",
              border: "none",
              padding: "8px 16px",
              borderRadius: "5px",
              cursor: "pointer"
            }}>Sil</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default UsersPage;
