import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [users, setUsers] = useState([]);
  const [machines, setMachines] = useState([]);
  const [faults, setFaults] = useState([]);
  const [userForm, setUserForm] = useState({ name: "", role: "", email: "", phone: "" });
  const [machineForm, setMachineForm] = useState({ name: "", type: "", status: "", location: "" });
  const [faultForm, setFaultForm] = useState({ machine_id: "", description: "", status: "open" });
  const [resolverMap, setResolverMap] = useState({});

  useEffect(() => {
    fetchUsers();
    fetchMachines();
    fetchFaults();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Kullanıcılar çekilemedi:", error);
    }
  };

  const fetchMachines = async () => {
    try {
      const response = await axios.get("http://localhost:5000/machines");
      setMachines(response.data);
    } catch (error) {
      console.error("Makineler çekilemedi:", error);
    }
  };

  const fetchFaults = async () => {
    try {
      const response = await axios.get("http://localhost:5000/faults");
      setFaults(response.data);
    } catch (error) {
      console.error("Arızalar çekilemedi:", error);
    }
  };

  const handleUserInputChange = (e) => {
    setUserForm({ ...userForm, [e.target.name]: e.target.value });
  };

  const handleMachineInputChange = (e) => {
    setMachineForm({ ...machineForm, [e.target.name]: e.target.value });
  };

  const handleFaultInputChange = (e) => {
    setFaultForm({ ...faultForm, [e.target.name]: e.target.value });
  };

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", userForm);
      setUserForm({ name: "", role: "", email: "", phone: "" });
      fetchUsers();
      toast.success("Kullanıcı başarıyla eklendi! 🎉");
    } catch (error) {
      console.error("Kullanıcı eklenemedi:", error);
      toast.error("Kullanıcı eklenemedi ❌");
    }
  };

  const handleMachineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/machines", machineForm);
      setMachineForm({ name: "", type: "", status: "", location: "" });
      fetchMachines();
      toast.success("Makine başarıyla eklendi! 🎉");
    } catch (error) {
      console.error("Makine eklenemedi:", error);
      toast.error("Makine eklenemedi ❌");
    }
  };

  const handleFaultSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/faults", faultForm);
      setFaultForm({ machine_id: "", description: "", status: "open" });
      fetchFaults();
      toast.success("Arıza başarıyla kaydedildi! 🎉");
    } catch (error) {
      console.error("Arıza eklenemedi:", error);
      toast.error("Arıza kaydı yapılamadı ❌");
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
      toast.error("Kullanıcı silindi! 🗑️");
    } catch (error) {
      console.error("Kullanıcı silinemedi:", error);
      toast.error("Kullanıcı silinemedi ❌");
    }
  };

  const deleteMachine = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/machines/${id}`);
      fetchMachines();
      toast.error("Makine silindi! 🗑️");
    } catch (error) {
      console.error("Makine silinemedi:", error);
      toast.error("Makine silinemedi ❌");
    }
  };

  const deleteFault = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/faults/${id}`);
      fetchFaults();
      toast.error("Arıza kaydı silindi! 🗑️");
    } catch (error) {
      console.error("Arıza kaydı silinemedi:", error);
      toast.error("Arıza kaydı silinemedi ❌");
    }
  };

  const resolveFault = async (id) => {
    try {
      const resolverId = resolverMap[id];

      if (!resolverId) {
        toast.error("Lütfen çözüm yapacak kullanıcıyı seçin!");
        return;
      }

      await axios.patch(`http://localhost:5000/faults/${id}`, {
        status: "resolved",
        resolved_by: resolverId
      });
      fetchFaults();
      toast.success("Arıza başarıyla çözüldü! 🎯");
    } catch (error) {
      console.error("Arıza çözülemedi:", error);
      toast.error("Arıza çözümünde hata oluştu ❌");
    }
  };

  return (
    <div style={{
      padding: "40px",
      backgroundColor: "#f5f7fa",
      minHeight: "100vh",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: "#333333"
    }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>Akıllı Talep ve Arıza Yönetimi</h1>

      {/* Kullanıcı Ekleme */}
      <div style={{
        backgroundColor: "#ffffff",
        padding: "20px",
        marginBottom: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h2>Kullanıcı Ekle</h2>
        <form onSubmit={handleUserSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
          <input type="text" name="name" placeholder="Ad Soyad" value={userForm.name} onChange={handleUserInputChange} required />
          <input type="text" name="role" placeholder="Rol" value={userForm.role} onChange={handleUserInputChange} required />
          <input type="email" name="email" placeholder="Email" value={userForm.email} onChange={handleUserInputChange} required />
          <input type="text" name="phone" placeholder="Telefon" value={userForm.phone} onChange={handleUserInputChange} />
          <button type="submit" style={{
            backgroundColor: "#4CAF50",
            color: "#ffffff",
            border: "none",
            padding: "10px 20px",
            borderRadius: "5px",
            cursor: "pointer"
          }}>Kaydet</button>
        </form>
      </div>

      {/* Kullanıcı Listesi */}
      <h3>Kullanıcılar</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {users.map((user) => (
          <li key={user.id} style={{
            backgroundColor: "#ffffff",
            marginBottom: "10px",
            padding: "15px",
            borderRadius: "8px",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center"
          }}>
            <span>{user.name} - {user.role} - {user.email} - {user.phone}</span>
            <button onClick={() => deleteUser(user.id)} style={{
              backgroundColor: "#f44336",
              color: "#ffffff",
              border: "none",
              padding: "8px 12px",
              borderRadius: "5px",
              cursor: "pointer"
            }}>Sil</button>
          </li>
        ))}
      </ul>

      {/* Makine Ekleme */}
      <div style={{
        backgroundColor: "#ffffff",
        padding: "20px",
        marginBottom: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h2>Makine Ekle</h2>
        <form onSubmit={handleMachineSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
          <input type="text" name="name" placeholder="Makine Adı" value={machineForm.name} onChange={handleMachineInputChange} required />
          <input type="text" name="type" placeholder="Tür" value={machineForm.type} onChange={handleMachineInputChange} required />
          <input type="text" name="status" placeholder="Durum" value={machineForm.status} onChange={handleMachineInputChange} />
          <input type="text" name="location" placeholder="Lokasyon" value={machineForm.location} onChange={handleMachineInputChange} />
          <button type="submit" style={{
            backgroundColor: "#4CAF50",
            color: "#ffffff",
            border: "none",
            padding: "10px 20px",
            borderRadius: "5px",
            cursor: "pointer"
          }}>Kaydet</button>
        </form>
      </div>

      {/* Makine Listesi */}
      <h3>Makineler</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {machines.map((machine) => (
          <li key={machine.id} style={{
            backgroundColor: "#ffffff",
            marginBottom: "10px",
            padding: "15px",
            borderRadius: "8px",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center"
          }}>
            <span>{machine.name} - {machine.type} - {machine.status} - {machine.location}</span>
            <button onClick={() => deleteMachine(machine.id)} style={{
              backgroundColor: "#f44336",
              color: "#ffffff",
              border: "none",
              padding: "8px 12px",
              borderRadius: "5px",
              cursor: "pointer"
            }}>Sil</button>
          </li>
        ))}
      </ul>

      {/* Arıza Ekleme */}
      <div style={{
        backgroundColor: "#ffffff",
        padding: "20px",
        marginBottom: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
      }}>
        <h2>Arıza Ekle</h2>
        <form onSubmit={handleFaultSubmit} style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
          <select name="machine_id" value={faultForm.machine_id} onChange={handleFaultInputChange} required style={{
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            flexGrow: 1
          }}>
            <option value="">Makine Seçiniz</option>
            {machines.map((machine) => (
              <option key={machine.id} value={machine.id}>
                {machine.name} - {machine.type}
              </option>
            ))}
          </select>
          <input type="text" name="description" placeholder="Açıklama" value={faultForm.description} onChange={handleFaultInputChange} required style={{
            flexGrow: 2,
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc"
          }} />
          <button type="submit" style={{
            backgroundColor: "#4CAF50",
            color: "#ffffff",
            border: "none",
            padding: "10px 20px",
            borderRadius: "5px",
            cursor: "pointer"
          }}>Kaydet</button>
        </form>
      </div>

      {/* Arıza Listesi */}
      <h3>Arızalar</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {faults.map((fault) => (
          <li key={fault.id} style={{
            backgroundColor: "#ffffff",
            marginBottom: "10px",
            padding: "15px",
            borderRadius: "8px",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center"
          }}>
            <span>
  Makine ID: {fault.machine_id} - Açıklama: {fault.description} - Durum: {fault.status}
  {fault.resolved_at && (
    <>
      <br />
      Çözüm Tarihi: {new Date(fault.resolved_at).toLocaleString()}
    </>
  )}
</span>

            <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
              <select
                value={resolverMap[fault.id] || ""}
                onChange={(e) => setResolverMap({ ...resolverMap, [fault.id]: e.target.value })}
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ccc"
                }}
              >
                <option value="">Çözecek kullanıcı seç</option>
                {users.map((user) => (
                  <option key={user.id} value={user.id}>
                    {user.name}
                  </option>
                ))}
              </select>

              {fault.status === "open" && (
                <button onClick={() => resolveFault(fault.id)} style={{
                  backgroundColor: "#4CAF50",
                  color: "#ffffff",
                  border: "none",
                  padding: "8px 12px",
                  borderRadius: "5px",
                  cursor: "pointer"
                }}>
                  Çöz
                </button>
              )}
              <button onClick={() => deleteFault(fault.id)} style={{
                backgroundColor: "#f44336",
                color: "#ffffff",
                border: "none",
                padding: "8px 12px",
                borderRadius: "5px",
                cursor: "pointer"
              }}>
                Sil
              </button>
            </div>
          </li>
        ))}
      </ul>

      <ToastContainer />
    </div>
  );
}

export default App;
