import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    role: "",
    email: "",
    phone: ""
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Kullanıcılar çekilemedi:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", formData);
      setFormData({ name: "", role: "", email: "", phone: "" }); // Formu temizle
      fetchUsers(); // Listeyi güncelle
    } catch (error) {
      console.error("Kullanıcı eklenemedi:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Akıllı Talep ve Arıza Yönetimi - Kullanıcılar</h1>

      {/* Kullanıcı Ekleme Formu */}
      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          name="name"
          placeholder="Ad Soyad"
          value={formData.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="role"
          placeholder="Rol (örneğin: engineer)"
          value={formData.role}
          onChange={handleInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
          style={{ marginLeft: "10px" }}
        />
        <input
          type="text"
          name="phone"
          placeholder="Telefon"
          value={formData.phone}
          onChange={handleInputChange}
          style={{ marginLeft: "10px" }}
        />
        <button type="submit" style={{ marginLeft: "10px" }}>Kaydet</button>
      </form>

      {/* Kullanıcı Listesi */}
      <ul>
        {users.length > 0 ? (
          users.map((user) => (
            <li key={user.id}>
              {user.name} - {user.role} - {user.email} - {user.phone}
            </li>
          ))
        ) : (
          <p>Henüz kullanıcı bulunamadı.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
