import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



function App() {
  const [users, setUsers] = useState([]);
  const [machines, setMachines] = useState([]);
  const [faults, setFaults] = useState([]);
  const [userForm, setUserForm] = useState({ name: "", role: "", email: "", phone: "" });
  const [machineForm, setMachineForm] = useState({ name: "", type: "", status: "", location: "" });
  const [faultForm, setFaultForm] = useState({ machine_id: "", description: "", status: "open" });

  useEffect(() => {
    fetchUsers();
    fetchMachines();
    fetchFaults();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("KullanÄ±cÄ±lar Ã§ekilemedi:", error);
    }
  };

  const fetchMachines = async () => {
    try {
      const response = await axios.get("http://localhost:5000/machines");
      setMachines(response.data);
    } catch (error) {
      console.error("Makineler Ã§ekilemedi:", error);
    }
  };

  const fetchFaults = async () => {
    try {
      const response = await axios.get("http://localhost:5000/faults");
      setFaults(response.data);
    } catch (error) {
      console.error("ArÄ±zalar Ã§ekilemedi:", error);
    }
  };

  const handleUserInputChange = (e) => {
    setUserForm({ ...userForm, [e.target.name]: e.target.value });
  };

  const handleMachineInputChange = (e) => {
    setMachineForm({ ...machineForm, [e.target.name]: e.target.value });
  };

  const handleFaultInputChange = (e) => {
    setFaultForm({ ...faultForm, [e.target.name]: e.target.value });
  };

  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/users", userForm);
      setUserForm({ name: "", role: "", email: "", phone: "" });
      fetchUsers();
      toast.success("KullanÄ±cÄ± baÅŸarÄ±yla eklendi! ðŸŽ‰");
    } catch (error) {
      console.error("KullanÄ±cÄ± eklenemedi:", error);
      toast.error("KullanÄ±cÄ± eklenemedi âŒ");
    }
  };


  const handleMachineSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/machines", machineForm);
      setMachineForm({ name: "", type: "", status: "", location: "" });
      fetchMachines();
      toast.success("Makine baÅŸarÄ±yla kaydedildi! ðŸŽ‰");
    } catch (error) {
      console.error("Makine eklenemedi:", error);
      toast.error("Makine eklenemedi âŒ");
    }
  };


  const handleFaultSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/faults", faultForm);
      setFaultForm({ machine_id: "", description: "", status: "open" });
      fetchFaults();
      toast.success("ArÄ±za baÅŸarÄ±yla kaydedildi! ðŸŽ‰");
    } catch (error) {
      console.error("ArÄ±za eklenemedi:", error);
      toast.error("ArÄ±za eklenemedi âŒ");
    }
  };


  // ðŸ”¥ KullanÄ±cÄ± Silme Fonksiyonu
  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
      toast.error("KullanÄ±cÄ± silindi! ðŸ—‘ï¸");
    } catch (error) {
      console.error("KullanÄ±cÄ± silinemedi:", error);
      toast.error("KullanÄ±cÄ± silinemedi âŒ");
    }
  };



  // ðŸ”¥ Makine Silme Fonksiyonu
  const deleteMachine = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/machines/${id}`);
      fetchMachines();
      toast.error("Makine silindi! ðŸ—‘ï¸");
    } catch (error) {
      console.error("Makine silinemedi:", error);
      toast.error("Makine silinemedi âŒ");
    }
  };



  // ðŸ”¥ ArÄ±za Silme Fonksiyonu
  const deleteFault = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/faults/${id}`);
      fetchFaults();
      toast.error("ArÄ±za kaydÄ± silindi! ðŸ—‘ï¸");
    } catch (error) {
      console.error("ArÄ±za kaydÄ± silinemedi:", error);
      toast.error("ArÄ±za kaydÄ± silinemedi âŒ");
    }
  };



  // ðŸ”¥ ArÄ±za Ã‡Ã¶zme Fonksiyonu
const resolveFault = async (id) => {
  try {
    await axios.patch(`http://localhost:5000/faults/${id}`, {
      status: "resolved",
      resolved_by: 1 // (Ã¶rnek Ã§Ã¶zen kiÅŸi ID'si)
    });
    fetchFaults();
  } catch (error) {
    console.error("ArÄ±za Ã§Ã¶zÃ¼lemedi:", error);
  }
};


  return (
    <div style={{ 
      padding: "40px", 
      backgroundColor: "#f5f7fa", 
      minHeight: "100vh",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: "#333333"
    }}>

      <h1>AkÄ±llÄ± Talep ve ArÄ±za YÃ¶netimi</h1>

      {/* KullanÄ±cÄ± Ekleme Formu */}
      <h2>KullanÄ±cÄ± Ekle</h2>
      <form onSubmit={handleUserSubmit} style={{ marginBottom: "20px" }}>
        <input type="text" name="name" placeholder="Ad Soyad" value={userForm.name} onChange={handleUserInputChange} required />
        <input type="text" name="role" placeholder="Rol" value={userForm.role} onChange={handleUserInputChange} required style={{ marginLeft: "10px" }} />
        <input type="email" name="email" placeholder="Email" value={userForm.email} onChange={handleUserInputChange} required style={{ marginLeft: "10px" }} />
        <input type="text" name="phone" placeholder="Telefon" value={userForm.phone} onChange={handleUserInputChange} style={{ marginLeft: "10px" }} />
        <button type="submit" style={{ marginLeft: "10px" }}>Kaydet</button>
      </form>

      {/* KullanÄ±cÄ± Listesi */}
      <h3>KullanÄ±cÄ± Listesi</h3>
      <ul>
